import React, { useState } from 'react';

export default function App() {
	const questions = [
		{
			questionText: 'What is the capital of France?',
			answerOptions: [
				{ answerText: 'New York', isCorrect: false },
				{ answerText: 'London', isCorrect: false },
				{ answerText: 'Paris', isCorrect: true },
				{ answerText: 'Dublin', isCorrect: false },
			],
		},
		{
			questionText: 'Who is CEO of Tesla?',
			answerOptions: [
				{ answerText: 'Jeff Bezos', isCorrect: false },
				{ answerText: 'Elon Musk', isCorrect: true },
				{ answerText: 'Bill Gates', isCorrect: false },
				{ answerText: 'Tony Stark', isCorrect: false },
			],
		},
		{
			questionText: 'The iPhone was created by which company?',
			answerOptions: [
				{ answerText: 'Apple', isCorrect: true },
				{ answerText: 'Intel', isCorrect: false },
				{ answerText: 'Amazon', isCorrect: false },
				{ answerText: 'Microsoft', isCorrect: false },
			],
		},
		{
			questionText: 'How many Harry Potter books are there?',
			answerOptions: [
				{ answerText: '1', isCorrect: false },
				{ answerText: '4', isCorrect: false },
				{ answerText: '6', isCorrect: false },
				{ answerText: '7', isCorrect: true },
			],
		},
	];

	const [currentQuestion, setCurrentQuestion] = useState(0);
	const [showScore, setShowScore] = useState(false);
	const [score, setScore] = useState(0);
	const [select, setSelect] = useState("");
 const [counter, setCounter] = React.useState(60);

 React.useEffect(() => {
  counter > 0 && setTimeout(() => setCounter(counter - 1), 1000);
 }, [counter]);

 const handleAnswerOptionClick = (isCorrect,id) => {
  setSelect(id)
  if (isCorrect) {
   setScore(score + 1);
  }
 }
 const handleSubmit = () => {
  const nextQuestion = currentQuestion + 1;
  setSelect("")
		if (nextQuestion < questions.length) {
			setCurrentQuestion(nextQuestion);
		} else {
			setShowScore(true);
		}
	};
	return (
		<div className='app'>
			{showScore ? (
				<div className='score-section'>
					You scored {score} out of {questions.length}
				</div>
			) : (
				<>
      <div className='question-section'>
						<div className='question-count'>
							<span>Question {currentQuestion + 1}</span>/{questions.length}
						</div>
						<div className='question-text'>{questions[currentQuestion].questionText}</div>
					</div>
					<div className='answer-section'>
      <div>Countdown: {counter}</div>
						{questions[currentQuestion].answerOptions.map((answerOption,id) => (
       <button
        key={id}
        onClick={() => handleAnswerOptionClick(answerOption.isCorrect,id)}
        style={{backgroundColor: select === id ? "red" : "",margin: "5px" }}
       >{answerOption.answerText}</button>
						))}
       <button style={{justifyContent:"center",marginLeft:"-380px",marginTop:"40px"}}onClick={handleSubmit}>Next</button>
      </div>
				</>
			)}
		</div>
	);
}
